
public class Divisbleby7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int count = 0;
		for (int i = 1; i <= 100; i++) {

			{
				if (i % 7 == 0){
					System.out.print(i + " ");
				count++;
				System.out.println(count);
			}

		}

	}

	}
}
